GoogleDrive::usage=
	"A standard head for accessing the Google Drive API";


Begin["`Private`"];


(* ::Subsection:: *)
(*Interface*)



$GoogleDriveCallListing={
	"List",
	"Search",
	"Info",
	"Upload",
	"Update",
	"Download",
	"DownloadURL",
	"Delete",
	"Permissions",
	"CreatePermissions",
	"UpdatePermissions",
	"DeletePermissions",
	"Publish",
	"Privatize"
	};
$GoogleDriveCalls=
	<|
		"list"->
			(GAFileInfo[]&),
		"search"->
			GAFileSearch,
		"info"->
			GAFileInfo,
		"upload"->
			GAFileUpload,
		"update"->
			GAFileUpdate,
		"download"->
			GAFileDownload,
		"downloadurl"->
			GAFileDownloadURL,
		"delete"->
			GAFileDelete,
		"permissions"->
			GAFilePermissions,
		"createpermissions"->
			GAFileCreatePermissions,
		"updatepermissions"->
			GAFileUpdatePermissions,
		"deletepermissions"->
			GAFileDeletePermissions,
		"publish"->
			GAFilePublish,
		"privatize"->
			GAFilePrivatize
		|>;
GoogleDriveCallQ[call_String]:=
	KeyMemberQ[$GoogleDriveCalls,ToLowerCase@call];


PackageAddAutocompletions[
	"GoogleDrive",
	{$GoogleDriveCallListing}
	];


GoogleDrive[
	applyCall:True|False:True,
	call_String?GoogleDriveCallQ,
	args___
	]:=
	Block[{
		$GAOAuthToken=
			Replace[$GAOAuthToken,Except[_String]->""],
		$GAOAuthTokenDataTmp
		},
		Block[{$GAApplyRequests=applyCall},
			$GoogleDriveCalls[ToLowerCase@call][args]
			]
		];


End[];



